sys_script_client.script:
function onLoad() {
	// Clear existing messages
    g_form.clearMessages();
	var employeeId = "SIFY00000001";
	// Call the function with the specific employeeId
	getEmployeeDetails(employeeId);
}


function getEmployeeDetails(employeeId) {
	// Create a GlideAjax object
	var ga = new GlideAjax('SifyEmployeeTest');
    ga.addParam('sysparm_name', 'getEmployee');
    ga.addParam('sysparm_employee_id', employeeId);
    ga.getXML(function(response) {
        var employeeDetails = JSON.parse(response.responseXML.documentElement.getAttribute('answer'));
		g_form.addInfoMessage(employeeDetails.designation);
        if (employeeDetails.error) {
            g_form.addErrorMessage(employeeDetails.error);
        } else {
            g_form.setValue('name', employeeDetails.name);
            g_form.setValue('email', employeeDetails.email);
			g_form.addFormMessage('Hi ' + employeeDetails.name + '!', 'info');
        }
    });
}
