sys_script.script:
(function executeRule(current, previous /*null when async*/) {
    // Check if the employee_id is not already set (i.e., for new records)
    if (current.employee_id) {
        return; // Skip if the employee_id is already set
    }

    // Find the maximum employee_id used so far
    var gr = new GlideRecord('x_1465873_sifydemo_sify_employees');
	gr.addNotNullQuery('employee_id'); // Exclude rows with empty employee_id
    gr.orderByDesc('employee_id'); // Sort in descending order to get the highest ID
    gr.setLimit(1); // Get only the highest ID
    gr.query();

    var maxNumber = 0;
    if (gr.next()) {
        var numberPart = gr.employee_id.substring(4); // Extract numeric part from the employee_id
        maxNumber = parseInt(numberPart, 10); // Convert it to an integer
    }

    var newNumber = 'SIFY' + (maxNumber + 1).toString().padStart(8, '0'); // Increment and format

    current.employee_id = newNumber; // Set the new employee_id
})(current, previous);
