sys_ws_operation.operation_script:
(function process(request, response) {

    var queryParameters = request.queryParams;
    var tableName = queryParameters.tableName;
    var employeeId = queryParameters.employeeId;


    var obj = {
        status: "NOT_FOUND",
        Name: null,
        Age: null,
        DOB: null,
        Designation: null,
        Email: null
    };

    if (tableName && employeeId) {
        var gr = new GlideRecord(tableName);

        gr.addQuery('employee_id', employeeId);
        gr.query();

        if (gr.next()) {
            obj.Age = gr.age;
            obj.DOB = gr.date_of_birth;
            obj.Designation = gr.designation;
            obj.Email = gr.email;
            obj.Name = gr.name;
            obj.status = "OK";
        }
    } else {
        obj.status = "ERROR: Missing tableName or employeeId";
    }

    response.setBody(obj);

})(request, response);
