sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

	var requestBody = request.body.data;
	var tableName = 'x_1465873_sifydemo_sify_employees';

    var obj = {
        status: "NOT_FOUND",
		message: "Failed to Create Record! Missing Dependencies",
    };

    if (requestBody) {
        var gr = new GlideRecord(tableName);
        gr.initialize();

        if (requestBody.Age) gr.age = parseInt(requestBody.Age);
        if (requestBody.DOB) gr.date_of_birth = requestBody.DOB;
        if (requestBody.Designation) gr.designation = requestBody.Designation;
        if (requestBody.Email) gr.email = requestBody.Email;
        if (requestBody.Name) gr.name = requestBody.Name;
		if(requestBody.Gender) gr.gender = requestBody.Gender;

        var sys_id = gr.insert();

        if (sys_id) {
            var grRetrieve = new GlideRecord(tableName);
            if (grRetrieve.get(sys_id)) {
                obj.status = "OK";
                obj.message = "Record inserted successfully";
                obj.employee_id = grRetrieve.employee_id;
            } else {
                obj.message = "Failed to retrieve the inserted record";
            }
        } else {
            obj.message = "Record insertion failed";
        }
    }

    response.setBody(obj);

})(request, response);
